insert into club(`name`,`introduction`,`detail`,`url`,`section`,`location`,`recruiting`)
values('P.O.E.','Primary Organization of English. 영어회화 동아리',
       '서강대학교 준 동아리 P.O.E.(Primary Organization of English)는 이번에 74기수를 맞이하는 뿌리 깊은 동아리로, 다양한 주제를 가지고 영어로 이야기를 나누고 외국인 친구들과 함께 한국 문화를 즐기는 곳입니다. 매주 수요일 오후 6시에 열리는 정기 모임에서는 간단한 free talk 후 식사를 하거나 한강 소풍, 벚꽃 및 축제 구경, 관광 명소 방문, MT 등의 친목 도모를 합니다. 또한 정기 모임 외에도 각종 번개 모임을 열거나 여행 모집을 하기도 한답니다!
저희 동아리는 교환 및 국제학생들과 한국인들의 언어 및 문화 교류를 목적으로 하는 만큼 한국에 오신 외국인 분들도 적극적으로 환영하며, 한국 학생과 국제 및 교환학생 비율은 1:1 정도로 맞추려고 하고 있습니다.
매 학기 신입부원 모집 기간마다 P.O.E.에 관심이 있지만 영어 실력이 다른 학우분들이나 국제 및 교환학생 분들에 비해 부족하다 느껴 지원을 망설이시는 분들이 꽤 많습니다. 하지만 실제로 동아리 부원들 중 영어를 제 1 언어로 사용하시는 분들은 극히 드물며, 동아리 활동을 하며 영어에 자신감을 갖고 회화 실력이 느신 분들도 상당히 많답니다. 때문에 영어실력에 대한 고민이 있으시다면 일단 동아리에 들어와 보시고 동아리가 본인과 잘 맞는지 경험해 보라고 말씀 드리고 싶어요.
외국인 친구들과 평생 남을 추억을 쌓고 싶으신 분, 외국의 문화가 궁금하신 분, 영어 울렁증을 즐겁게 극복하고 싶으신 분, 다양한 과와 연령대의 사람들과 친해지고 싶으신 분, 인생 동아리를 찾고 싶으신 분 모두 환영합니다!!',
       'https://sgdongyeon.oopy.io/615b7120-c8c9-4a42-a269-362a8f59eb9f','학술분과','',false);
insert into club(`name`,`introduction`,`detail`,`url`,`section`,`location`,`recruiting`)
values('SGCC','Sogang Computer Club Club for learning computer',
       'SGCC는 서강대를 대표하는 중앙 컴퓨터 동아리로서 컴퓨터에 대한 지식의 깊이에 상관없이 컴퓨터에 관심있는 사람들을 위해 만들어진 동아리입니다. 보통의 경우 오프라인으로 회원들끼리 모여 파이썬, C언어, 자료구조, 알고리즘 스터디처럼 지식을 나누는 스터디를 진행하여 많은 동아리원들이 자신이 듣는 수업에 많은 도움을 받습니다. 나아가 웹/앱 스터디를 통해 자신이 만들고 싶은 서비스를 동아리원과 함께 만들고 이를 통해 자신의 아이디어를 직접 구현하고 팀을 이뤄 좀 더 나은 결과물을 위해 논의하는 경험도 할 수 있습니다. 하지만 2020년 예상치 못한 코로나로 인해 대면으로 동아리 활동을 하기 어려워지면서 온라인으로 스터디를 진행하였고 주로 멘토에게 질의응답을 하고 공부 방향을 제시해주는 방식으로 2020 스터디를 진행하였습니다. 코로나가 장기화되고 있는 지금, 포스트 코로나에 대비해 온라인으로 회원들끼리 소통하고 함께 발전할 수 있는 방향을 모색중입니다.
같이 무언가를 하기 이전에 동아리원들의 교류를 늘리기 위해 디스코드에 모여 자유롭게 자신의 공부를 하며 서로 질문하고 공부 이외의 여러 즐거운 얘기를 할 수 있는 ‘모각코’라는 활동을 진행중에 있습니다. 또한 컴퓨터를 하다보면 막히는 것이 생기기 마련인데 오프라인으로 만나서 질문할 수 없기 때문에 좀 더 자유로운 질의응답이 오고 갈 수 있도록 익명 질문방 개설을 추진하고 있습니다. 저희 동아리에는 컴퓨터 각 분야에 능통한 선배들이 굉장히 많고 다들 후배들에게 열린 마음으로 다가와 주시기 때문에 동아리원들이 좀 더 편하게 자신의 질문을 해결할 수 있을 것입니다.',
       'https://sgdongyeon.oopy.io/9e7780d8-b995-4dfb-83a7-6500764d7e13','학술분과','EB128',false);

